import time
import random 

matrix_comp, matrix_swap = 0, 0

def shellSort(array):
    global matrix_swap
    global matrix_comp
    try:
        array = [int(i) for i in array]
    except ValueError:
        pass
    start_time = time.time()
    comparison_count = 0 
    swap_count = 0
    n = len(array)
    k = n//2
    interval = 2**k -1
    while interval > 0:
        for i in range(interval, n):
            temp = array[i]
            j = i
            while j >= interval and array[j - interval] > temp:
                comparison_count += 1
                array[j] = array[j - interval]
                j -= interval
            array[j] = temp
            swap_count += 1
        k -= 1
        interval = 2**k -1
    if MATRIX == True:
        matrix_swap += swap_count
        matrix_comp += comparison_count
    if MATRIX != True:
        print("--- %s seconds ---" % (time.time() - start_time))
        print("comparisons = ", comparison_count)
        print("swaps = ", swap_count)
    return array

def mycomb(arr):
    global matrix_swap
    global matrix_comp
    try:
        arr = [int(i) for i in arr]
    except ValueError:
        pass
    start_time = time.time()
    comparison_count = 0 
    swap_count = 0
    gap = int(len(arr))
    swap = True

    while gap > 1 or swap:
        gap = int(gap/1.3)
        swap = False
        for i in range(len(arr)-gap):
            comparison_count += 1
            if arr[i]>arr[i+gap]:
                swap_count += 1
                arr[i], arr[i+gap] = arr[i+gap], arr[i]
                swap = True
    if MATRIX == True:
        matrix_swap += swap_count
        matrix_comp += comparison_count
    if MATRIX != True:
        print("--- %s seconds ---" % (time.time() - start_time))
        print("comparisons = ", comparison_count)
        print("swaps = ", swap_count)
    return arr


